

function areSameDataType(current, input) {
  console.log('----- %s --- %s ---------------------', current, input)
  console.log(Object.getPrototypeOf(current))
  console.log(Object.getPrototypeOf(input))
  if (Object.getPrototypeOf(current) === Object.getPrototypeOf(input)) {
    return true
  } else {
    return false
  }
}


console.log(areSameDataType('x', 'a'))

