'use strict';

var fs = require('fs') // need only for the examples
var updater = require('../') // pass the JSON file to work on

function getParsedPackage() {
  return JSON.parse(fs.readFileSync(__dirname + '/settings.json'))
}

updater(__dirname + '/settings.json').append('mod.ules', {x: 89}, function(err) {
  if (err) return console.log(err)
  var pkg = getParsedPackage()
  console.log('\n\n-------------')
  console.log(pkg)
  console.log()
})

